import React, { useEffect, useState } from 'react'
import axios from "./axios";
import "./row.css";
import PlayCircleFilledIcon from '@material-ui/icons/PlayCircleFilled';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import ThumbUpIcon from '@material-ui/icons/ThumbUp';
import ThumbDownIcon from '@material-ui/icons/ThumbDown';
function Row({title,fetchurl}){
    const [rdata,setdata]=useState([]);
    useEffect(()=>{
        const rowdata=async()=>{
            const {data}=await axios.get(fetchurl);
            setdata(data.results);
        }
        rowdata();
    },[])
    console.log(rdata);
    return (
        <div className="rowdiv">
            <h2 id={title}>{title}</h2>
            <div className="inrowdiv">
            {rdata.map((element)=>(
                <div className="movie" key={element.id} style={{backgroundImage:`url("https://image.tmdb.org/t/p/original/${element?.poster_path || element?.backdrop_path}")`}}>
                    <div className="bigshow">
                        <img src={`https://image.tmdb.org/t/p/original/${element?.poster_path || element?.backdrop_path}`} alt=""></img>
                        {/* <div className="icons">
                            <PlayCircleFilledIcon style={{color:"white"}} fontSize="large"></PlayCircleFilledIcon>
                             <CheckCircleOutlineIcon style={{color:"white"}} fontSize="large" className="icon"></CheckCircleOutlineIcon>
                             <ThumbUpIcon style={{color:"white"}} fontSize="large" className="icon"></ThumbUpIcon>
                             <ThumbDownIcon style={{color:"white"}}  fontSize="large" className="icon"/>   
                        </div>
                        <h4>{element?.vote_average}</h4> */}
                    </div>
                </div>

            ))}
            {/* <div className="leftscroll"><ArrowBackIosIcon className="left" fontSize="large"></ArrowBackIosIcon></div>
            <div className="rightscroll"><ArrowForwardIosIcon className="right" fontSize="small"></ArrowForwardIosIcon></div> */}
            </div>   
        </div>
    )
}
export default Row;
