import React, { useState } from 'react';
import './App.css';
import {BrowserRouter as Router,Switch,Route} from "react-router-dom";
import Login from "./loginpage";
import Home from "./home";
import Signin from "./siginpage";
import Profile from "./Profile";
import Signup from "./signup";
import {selectemail} from "./features/user/userSlice";
import {useSelector} from "react-redux";
export const r=React.createContext("");
function App() {
  // const [u,setu] = useState(false);
  // const u=useSelector(selectemail);
  // console.log(u);
  const [u,setu]=useState(true);
  return (
    <r.Provider value={{u,setu}}>
      <>
    <Router>
      <Switch>
      <Route path="/" exact component={u? Home:Login}></Route>
      <Route path="/home" exact component={u? Home:Signin}></Route>
      <Route path="/login"><Login/></Route>
      <Route path="/signin"><Signin/></Route>
      <Route path="/signup/:id"><Signup/></Route>
      <Route path="/profile"><Profile/></Route>
    </Switch>
    </Router>
    </>
    </r.Provider>
    // <>
